INSERT INTO ST_FCP_PROJ ( NA_PROJ_DETAILS_SID, PERIOD_SID, PRICE_TYPE, PROJECTION_PRICE, ADJUSTMENT, NOTES, USER_ID, SESSION_ID, LAST_MODIFIED_DATE) SELECT A.NA_PROJ_DETAILS_SID, A.PERIOD_SID, A.PRICE_TYPE, A.PROJECTION_PRICE, A.ADJUSTMENT, A.NOTES, ?UID USER_ID, ?SID SESSION_ID, '?LD' LAST_MODIFIED_DATE FROM dbo.FCP_PROJ A, dbo.NA_PROJ_DETAILS B WHERE A.NA_PROJ_DETAILS_SID=B.NA_PROJ_DETAILS_SID AND B.NA_PROJ_MASTER_SID=?PID;
INSERT INTO ST_PHS_ACTUALS ( NA_PROJ_DETAILS_SID, PERIOD_SID, PRICE_TYPE, ACTUAL_PRICE, NOTES, USER_ID, SESSION_ID, LAST_MODIFIED_DATE) SELECT A.NA_PROJ_DETAILS_SID, A.PERIOD_SID, A.PRICE_TYPE, A.ACTUAL_PRICE, A.NOTES, ?UID USER_ID, ?SID SESSION_ID, '?LD' LAST_MODIFIED_DATE FROM dbo.PHS_ACTUALS A, dbo.NA_PROJ_DETAILS B WHERE A.NA_PROJ_DETAILS_SID=B.NA_PROJ_DETAILS_SID AND B.NA_PROJ_MASTER_SID=?PID;
