		  DELETE A FROM RETURNS_DETAILS A JOIN (SELECT DISTINCT PROJECTION_MASTER_SID FROM @PROJECTION_RET)B ON A.PROJECTION_MASTER_SID=B.PROJECTION_MASTER_SID; 
