entity.query:
IF Object_id('TEMPDB..#SELECTED_HIERARCHY_NO') IS NOT NULL DROP TABLE #SELECTED_HIERARCHY_NO CREATE TABLE #SELECTED_HIERARCHY_NO (HIERARCHY_NO varchar(50),CCP_DETAILS_SID INT)
INSERT INTO #SELECTED_HIERARCHY_NO (HIERARCHY_NO, CCP_DETAILS_SID) SELECT HIERARCHY_NO,CH.CCP_DETAILS_SID FROM (VALUES [?HIERARCHY_NO_VALUES])A(HIERARCHY_NO) JOIN ST_CCP_HIERARCHY CH ON CH.[?HIERARCHY_COLUMN] LIKE A.HIERARCHY_NO+'%' [?TAB_BASED_JOIN]
SELECT COUNT(DISTINCT HIERARCHY_NO) FROM #SELECTED_HIERARCHY_NO;

IF Object_id('TEMPDB..#SELECTED_HIERARCHY_NO') IS NOT NULL DROP TABLE #SELECTED_HIERARCHY_NO CREATE TABLE #SELECTED_HIERARCHY_NO (HIERARCHY_NO varchar(50),CCP_DETAILS_SID INT)
INSERT INTO #SELECTED_HIERARCHY_NO (HIERARCHY_NO, CCP_DETAILS_SID) SELECT HIERARCHY_NO,CH.CCP_DETAILS_SID FROM (VALUES [?HIERARCHY_NO_VALUES])A(HIERARCHY_NO) JOIN ST_CCP_HIERARCHY CH ON CH.[?HIERARCHY_COLUMN] LIKE A.HIERARCHY_NO+'%' [?TAB_BASED_JOIN]
SELECT DISTINCT HIERARCHY_NO FROM #SELECTED_HIERARCHY_NO ORDER BY HIERARCHY_NO OFFSET [?START] ROWS FETCH NEXT [?END] ROWS ONLY;
