entity.query:
DELETE CCM FROM dbo.custom_ccp_map CCM JOIN custom_view_details CVD ON CVD.custom_view_details_sid = CCM.custom_view_details_sid AND CVD.custom_view_master_sid IN(SELECT custom_view_master_sid FROM custom_view_master cvm JOIN @PROJECTION B ON B.projection_master_sid = cvm.projection_master_sid);
DELETE CRM FROM dbo.custom_relationship_builder CRM JOIN custom_view_details CVD ON CVD.custom_view_details_sid = CRM.custom_view_details_sid AND CVD.custom_view_master_sid IN(SELECT custom_view_master_sid FROM custom_view_master cvm JOIN @PROJECTION B ON B.projection_master_sid = cvm.projection_master_sid);
delete CVD from dbo.CUSTOM_VIEW_DETAILS CVD where
CVD.CUSTOM_VIEW_MASTER_SID IN(SELECT CUSTOM_VIEW_MASTER_SID
FROM CUSTOM_VIEW_MASTER cvm
JOIN @PROJECTION B
ON B.PROJECTION_MASTER_SID = cvm.PROJECTION_MASTER_SID);




delete CVD from dbo.CUSTOM_VIEW_DETAILS CVD where

CVD.CUSTOM_VIEW_MASTER_SID IN( Select PROJECTION_MASTER_SID from CUSTOM_VIEW_MASTER where PROJECTION_MASTER_SID = @PROJECTIONSID);

DELETE FROM dbo.CUSTOM_VIEW_MASTER WHERE PROJECTION_MASTER_SID = @PROJECTIONSID;
