entity.query:
IF Object_id('TEMPDB..#SELECTED_REBATE') IS NOT NULL DROP TABLE #SELECTED_REBATE
CREATE TABLE #SELECTED_REBATE ( RS_CONTRACT_SID INT )
INSERT INTO #SELECTED_REBATE (RS_CONTRACT_SID) SELECT RS_CONTRACT_SID FROM (VALUES [?RS_CONTRACT_SID])A(RS_CONTRACT_SID);

IF Object_id('TEMPDB..#SELECTED_HIERARCHY_NO') IS NOT NULL DROP TABLE #SELECTED_HIERARCHY_NO
CREATE TABLE #SELECTED_HIERARCHY_NO ( HIERARCHY_NO VARCHAR(50), CCP_DETAILS_SID INT )
INSERT INTO #SELECTED_HIERARCHY_NO (HIERARCHY_NO, CCP_DETAILS_SID) SELECT HIERARCHY_NO, CH.CCP_DETAILS_SID FROM ( SELECT HIERARCHY_NO FROM (VALUES [?HIERARCHY_NO_VALUES])A(HIERARCHY_NO) WHERE EXISTS (SELECT 1 FROM ST_NM_DISCOUNT_PROJ_MASTER MAS JOIN ST_CCP_HIERARCHY CH ON MAS.CCP_DETAILS_SID = CH.CCP_DETAILS_SID JOIN #SELECTED_REBATE SR ON SR.RS_CONTRACT_SID = MAS.RS_CONTRACT_SID WHERE CH.[?HIERARCHY_COLUMN] LIKE A.HIERARCHY_NO+'%' [?USER_GROUP_JOIN]))A [?SELECTED_HIERARCHY_JOIN];

IF Object_id('TEMPDB..#SELECTED_HIERARCHY_NO') IS NOT NULL DROP TABLE #SELECTED_HIERARCHY_NO
CREATE TABLE #SELECTED_HIERARCHY_NO ( HIERARCHY_NO VARCHAR(50), CCP_DETAILS_SID INT )
INSERT INTO #SELECTED_HIERARCHY_NO (HIERARCHY_NO, CCP_DETAILS_SID) SELECT HIERARCHY_NO, CH.CCP_DETAILS_SID FROM ( SELECT HIERARCHY_NO FROM (VALUES [?HIERARCHY_NO_VALUES])A(HIERARCHY_NO) WHERE EXISTS (SELECT 1 FROM ST_NM_DISCOUNT_PROJ_MASTER MAS JOIN ST_CCP_HIERARCHY CH ON MAS.CCP_DETAILS_SID = CH.CCP_DETAILS_SID JOIN #SELECTED_REBATE SR ON SR.RS_CONTRACT_SID = MAS.RS_CONTRACT_SID WHERE CH.[?CURRENT_HIERARCHY_NO] LIKE A.HIERARCHY_NO+'%' [?PREVIOUS_HIERARCHY_NO]))A JOIN ST_CCP_HIERARCHY CH ON CH.[?CURRENT_HIERARCHY_NO] LIKE A.HIERARCHY_NO + '%' [?PREVIOUS_HIERARCHY_NO];

DECLARE @VAR CHAR(1) = 'R' DECLARE @FREQUENCY CHAR(1) = '[?FREQUENCY]' IF Object_id( 'TEMPDB.DBO.#PERIOD', 'U' ) IS NOT NULL DROP TABLE #PERIOD SELECT PERIOD_SID, YEAR, PERIOD = CASE WHEN @FREQUENCY = 'M' THEN MONTH WHEN @FREQUENCY = 'Q' THEN QUARTER WHEN @FREQUENCY = 'S' THEN SEMI_ANNUAL ELSE YEAR END INTO #PERIOD FROM PERIOD;
IF Object_id('TEMPDB..#SELECTED_REBATE') IS NOT NULL DROP TABLE #SELECTED_REBATE CREATE TABLE #SELECTED_REBATE( RS_CONTRACT_SID int ) INSERT INTO #SELECTED_REBATE(RS_CONTRACT_SID) SELECT RS_CONTRACT_SID FROM ( VALUES [?RS_CONTRACT_SID] ) A(RS_CONTRACT_SID);
IF Object_id('TEMPDB..#SELECTED_HIERARCHY_NO') IS NOT NULL DROP TABLE #SELECTED_HIERARCHY_NO CREATE TABLE #SELECTED_HIERARCHY_NO( HIERARCHY_NO VARCHAR(50), CCP_DETAILS_SID INT, DISCOUNT VARCHAR(500), RS_CONTRACT_SID INT ) INSERT INTO #SELECTED_HIERARCHY_NO( CCP_DETAILS_SID, DISCOUNT, RS_CONTRACT_SID ) SELECT DISTINCT S.CCP_DETAILS_SID, CASE WHEN @VAR = 'R' THEN RS_NAME ELSE PRICE_GROUP_TYPE END, S.RS_CONTRACT_SID FROM ST_NM_DISCOUNT_PROJ_MASTER S JOIN #SELECTED_REBATE R ON S.RS_CONTRACT_SID = R.RS_CONTRACT_SID JOIN RS_CONTRACT RS ON RS.RS_CONTRACT_SID = R.RS_CONTRACT_SID JOIN CCP_DETAILS CD ON CD.CCP_DETAILS_SID = S.CCP_DETAILS_SID;
IF OBJECT_ID('TEMPDB..#EXFACTORY') IS NOT NULL DROP TABLE #EXFACTORY SELECT SUM( EXFACTORY_ACTUAL_SALES ) EXFACTORY_ACTUAL_SALES, SUM( EXFACTORY_FORECAST_SALES ) EXFACTORY_FORECAST_SALES, PERIOD, YEAR INTO #EXFACTORY FROM ST_PRODUCT_FILE A JOIN( SELECT DISTINCT ITEM_MASTER_SID
FROM ST_NM_DISCOUNT_PROJ_MASTER S JOIN #SELECTED_REBATE R ON S.RS_CONTRACT_SID = R.RS_CONTRACT_SID JOIN RS_CONTRACT RS ON RS.RS_CONTRACT_SID = R.RS_CONTRACT_SID JOIN CCP_DETAILS CD ON CD.CCP_DETAILS_SID = S.CCP_DETAILS_SID ) B ON A.ITEM_MASTER_SID = B.ITEM_MASTER_SID JOIN #PERIOD P ON P.PERIOD_SID = A.PERIOD_SID GROUP BY PERIOD, YEAR IF OBJECT_ID('TEMPDB..#ACTUAL_SALES') IS NOT NULL DROP TABLE #ACTUAL_SALES SELECT I.PERIOD, I.YEAR, ACTUAL_SALES = SUM( ACTUAL_SALES ), ACTUAL_UNITS = SUM( ACTUAL_UNITS ) INTO #ACTUAL_SALES FROM ST_NM_ACTUAL_SALES S INNER JOIN (select distinct ccp_details_sid from #SELECTED_HIERARCHY_NO) C ON C.CCP_DETAILS_SID = S.CCP_DETAILS_SID INNER JOIN #PERIOD I ON I.PERIOD_SID = S.PERIOD_SID GROUP BY I.PERIOD, I.YEAR IF Object_id('TEMPDB..#ACTUAL_DISCOUNT') IS NOT NULL DROP TABLE #ACTUAL_DISCOUNT SELECT I.PERIOD, I.YEAR, DISCOUNT_ACTUALS = Sum( ACTUAL_SALES ) INTO #ACTUAL_DISCOUNT FROM ST_NM_ACTUAL_DISCOUNT S JOIN RS_CONTRACT rs ON rs.RS_CONTRACT_SID = S.RS_CONTRACT_SID INNER JOIN #PERIOD I ON I.PERIOD_SID = S.PERIOD_SID JOIN ST_NM_DISCOUNT_PROJ_MASTER m ON m.RS_CONTRACT_SID = s.RS_CONTRACT_SID AND m.ccp_details_sid = s.ccp_details_sid WHERE EXISTS( SELECT 1 FROM #SELECTED_REBATE sr WHERE sr.RS_CONTRACT_SID = s.RS_CONTRACT_SID ) GROUP BY I.PERIOD, I.YEAR IF Object_id('TEMPDB..#PROJECTION_SALES') IS NOT NULL DROP TABLE #PROJECTION_SALES SELECT I.PERIOD, I.YEAR, PROJECTION_SALES = Sum( PROJECTION_SALES ), PROJECTION_UNITS = Sum( PROJECTION_UNITS ) INTO #PROJECTION_SALES FROM ST_NM_SALES_PROJECTION S INNER JOIN (select distinct ccp_details_sid from #SELECTED_HIERARCHY_NO) C ON C.CCP_DETAILS_SID = S.CCP_DETAILS_SID INNER JOIN #PERIOD I ON I.PERIOD_SID = S.PERIOD_SID GROUP BY I.PERIOD, I.YEAR IF Object_id('TEMPDB..#DISCOUNT_PROJECTION') IS NOT NULL DROP TABLE #DISCOUNT_PROJECTION SELECT I.PERIOD, I.YEAR, PROJECTED_DISCOUNT = Sum( PROJECTION_SALES ) INTO #DISCOUNT_PROJECTION FROM ST_NM_DISCOUNT_PROJECTION S JOIN RS_CONTRACT rs ON rs.RS_CONTRACT_SID = S.RS_CONTRACT_SID INNER JOIN #PERIOD I ON I.PERIOD_SID = S.PERIOD_SID JOIN ST_NM_DISCOUNT_PROJ_MASTER m ON m.RS_CONTRACT_SID = s.RS_CONTRACT_SID AND m.ccp_details_sid = s.ccp_details_sid WHERE EXISTS( SELECT 1 FROM #SELECTED_REBATE sr WHERE sr.RS_CONTRACT_SID = s.RS_CONTRACT_SID ) GROUP BY I.PERIOD, I.YEAR SELECT 'Projection Total' AS CHANGE_VALUE, AD.YEAR, AD.PERIOD, COALESCE( DISCOUNT_ACTUALS / NULLIF( ACTUAL_SALES, 0 ), 0 ) * 100 AS ACTUAL_RATE, DISCOUNT_ACTUALS AS ACTUAL_SALES, COALESCE( DISCOUNT_ACTUALS / NULLIF( ACTUAL_UNITS, 0 ), 0 ) AS ACTUAL_RPU, NULL AS PROJECTION_RATE, null AS PROJECTION_SALES, NULL AS PROJECTION_RPU, 0 AS AP_TABLE_INDICATOR, 'Projection Total' AS PROJECTION_TOTAL, COALESCE( DISCOUNT_ACTUALS / NULLIF( EXFACTORY_ACTUAL_SALES, 0 ), 0 ) * 100 AS EXFACT_DOLLAR_ACTUAL_PERCENT, NULL AS EXFACT_DOLLAR_FORE_PERCENT FROM #ACTUAL_DISCOUNT AD INNER JOIN #ACTUAL_SALES SA ON SA.YEAR = AD.YEAR AND SA.PERIOD = AD.PERIOD JOIN #EXFACTORY E ON E.PERIOD = SA.PERIOD AND E.YEAR = SA.YEAR UNION ALL SELECT 'Projection Total' AS CHANGE_VALUE, AD.YEAR, AD.PERIOD, NULL AS ACTUAL_RATE, NULL AS ACTUAL_SALES, NULL AS ACTUAL_RPU, COALESCE( PROJECTED_DISCOUNT / NULLIF( PROJECTION_SALES, 0 ), 0 ) * 100 AS PROJECTION_RATE, PROJECTED_DISCOUNT AS PROJECTION_SALES, COALESCE( PROJECTED_DISCOUNT / NULLIF( PROJECTION_UNITS, 0 ), 0 ) AS PROJECTION_RPU, 1 AS AP_TABLE_INDICATOR, 'Projection Total' AS PROJECTION_TOTAL, NULL AS EXFACT_DOLLAR_ACTUAL_PERCENT, COALESCE( PROJECTED_DISCOUNT / NULLIF(COALESCE(EXFACTORY_FORECAST_SALES,EXFACTORY_ACTUAL_SALES), 0), 0) * 100 AS EXFACT_DOLLAR_FORE_PERCENT FROM #DISCOUNT_PROJECTION AD INNER JOIN #PROJECTION_SALES SA ON SA.YEAR = AD.YEAR AND SA.PERIOD = AD.PERIOD JOIN #EXFACTORY E ON E.PERIOD = SA.PERIOD AND E.YEAR = SA.YEAR ORDER BY YEAR, PERIOD, AP_TABLE_INDICATOR;
